var process_flow,
    flows,
    selected_id = "";

process_flow = new GlideRecord('x_snd_ipf_process_flow');
process_flow.addQuery('table', current.getRecordClassName());
process_flow.addQuery('active', true);
process_flow.orderBy('order');
process_flow.query();

while (process_flow.next()) {
  if (GlideFilter.checkRecord(current, process_flow.condition)) {
    break;
  }
}

flows = new GlideRecord('x_snd_ipf_process_flow_element');
flows.addQuery('process_flow', process_flow.sys_id);
flows.addQuery('table', current.getRecordClassName());
flows.addQuery('active', true);
flows.orderBy('order');
flows.query();

j2:while.g2:evaluate:
var evaluator,
    is_past = false,
    is_available = false,
    is_current = false,
    id,
    on_click;

evaluator = new GlideScopedEvaluator();
evaluator.putVariable('current', current);

is_past = is_current == true ? true : false;

is_available = evaluator.evaluateScript(flows, 'access_script');
is_current = GlideFilter.checkRecord(current, flows.condition);

id = flows.table + '_' + flows.name;

if (is_available) {
  on_click = flows.client_script + ';advancedProcessFlowControl(\'' + 
    flows.table + '_' + flows.name + '\');';
} else {
  on_click = "";
}

selected_id = is_current ? id : selected_id;

j:jelly.script:
var old_id = $[selected_id].id,
    previous_id = $[selected_id].id;

// Control script for onClick
function advancedProcessFlowControl(new_id) {
  $(new_id).className = "select";
  if (new_id == old_id) {
    g_form.save();
  } else {
    $(old_id).className = old_id == previous_id ? "selected" : "available";
  }
  old_id = new_id;
}
